mapper.select:
select count(*)
from user
where email = #{email}

mapper.update:
update user
set email = #{email}, pw=#{pw}, nickname=#{nickname}, gender = #{gender}, mbti = #{mbti}
, interest1= #{interest1}, interest2=#{interest2}, interest3=#{interest3}, interest4=#{interest4}, interest5= #{interest5}
where email = #{email}
